var sl = require('../');
var test = require('tap').test;

test('API surface', function(t) {
  t.test('v1.0.0 API', function(t) {
    t.type(sl, 'function', 'top level export is a function');
    t.end();
  });

  t.test('v1.1.0 API', function(t) {
    t.type(sl, 'function', 'module is a function');
    t.type(sl.License, 'function', 'module.License is a function');
    t.strictEqual(sl, sl.License, 'module.License same as module');
    t.end();
  });

  t.end();
});
