"use strict";

module.exports.toMicros = toMicros
module.exports.diff = diff
module.exports.diffMicros = diffMicros

function toMicros(ts) {
  if (ts == null) {
    return 0
  }
  return (ts[0] * 1e6) + ((ts[1] / 1000) | 0)
}

function diff(start, end) {
  return [end[0] - start[0], end[1] - start[1]]
}

function diffMicros(start, end) {
  return toMicros(diff(start, end))
}
