"use strict";

module.exports = wrap

function wrap(tracer) {
  var dns = require("dns")

  dns.lookup = tracer.wrapCoreLinkerFirstArgString("dns", "lookup", dns.lookup)
  dns.resolve = tracer.wrapCoreLinkerFirstArgString("dns", "resolve", dns.resolve)
  dns.resolve4 = tracer.wrapCoreLinkerFirstArgString("dns", "resolve4", dns.resolve4)
  dns.resolve6 = tracer.wrapCoreLinkerFirstArgString("dns", "resolve6", dns.resolve6)
  dns.resolveMx = tracer.wrapCoreLinkerFirstArgString("dns", "resolveMx", dns.resolveMx)
  dns.resolveTxt = tracer.wrapCoreLinkerFirstArgString("dns", "resolveTxt", dns.resolveTxt)
  dns.resolveSrv = tracer.wrapCoreLinkerFirstArgString("dns", "resolveSrv", dns.resolveSrv)
  dns.resolveNs = tracer.wrapCoreLinkerFirstArgString("dns", "resolveNs", dns.resolveNs)
  dns.resolveCname = tracer.wrapCoreLinkerFirstArgString("dns", "resolveCname", dns.resolveCname)
  dns.reverse = tracer.wrapCoreLinkerFirstArgString("dns", "reverse", dns.reverse)
}
