"use strict";

module.exports = wrap

function wrap(tracer) {
  var zlib = require("zlib")

  zlib.deflate = tracer.wrapCoreLinker("zlib", "defalate", zlib.deflate)
  zlib.deflateRaw = tracer.wrapCoreLinker("zlib", "defalateRaw", zlib.deflateRaw)
  zlib.gzip = tracer.wrapCoreLinker("zlib", "gzip", zlib.gzip)
  zlib.gunzip = tracer.wrapCoreLinker("zlib", "gunzip", zlib.gunzip)
  zlib.inflate = tracer.wrapCoreLinker("zlib", "inflate", zlib.inflate)
  zlib.inflateRaw = tracer.wrapCoreLinker("zlib", "inflateRaw", zlib.inflateRaw)
  zlib.unzip = tracer.wrapCoreLinker("zlib", "unzip", zlib.unzip)
}
